import { Statement } from '@/types/statement';
import { formatCurrency } from '@/utils/currency';
import { formatDate } from '@/utils/dates';

const maskAccountNumber = (accountNumber: string) => {
  if (accountNumber.length <= 4) {
    return accountNumber;
  }
  const lastFour = accountNumber.slice(-4);
  return `XXXXXXXX${lastFour}`;
};

const formatStatementDate = (date: string) => formatDate(date);

// ICICI Bank logo - inline SVG
const buildIciciLogo = () => `
  <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="300" height="50" viewBox="0 0 646 99">
                    <image xlink:href="data:image/png;base64," x="0" y="0" width="646" height="99"/>
                  </svg>
  </div>
`;

export const renderIciciTemplate = (statement: Statement) => {
  const { details, transactions, meta } = statement;
  
  // Use period dates from meta if available, otherwise calculate from transactions
  const startDate = meta.statementPeriodStart ?? (transactions.length ? transactions[0].date : meta.generatedAt);
  const endDate = meta.statementPeriodEnd ?? (transactions.length ? transactions[transactions.length - 1].date : meta.generatedAt);

  const totalCredit = transactions.reduce((sum, txn) => sum + txn.credit, 0);
  const totalDebit = transactions.reduce((sum, txn) => sum + txn.debit, 0);
  const closingBalance = transactions.length
    ? transactions[transactions.length - 1].balance
    : details.startingBalance;

  // Pagination: 20 transactions per page
  const TRANSACTIONS_PER_PAGE = 17;
  const totalPages = Math.ceil(transactions.length / TRANSACTIONS_PER_PAGE);
  
  const buildPage = (pageNum: number, pageTxns: typeof transactions, isFirstPage: boolean, isLastPage: boolean) => {
    const pageCredit = pageTxns.reduce((sum, txn) => sum + txn.credit, 0);
    const pageDebit = pageTxns.reduce((sum, txn) => sum + txn.debit, 0);
    
    const htmlTransactions = pageTxns
      .map(
        (txn) => `
          <tr>
            <td>${formatStatementDate(txn.date)}</td>
            <td>${txn.reference || 'CHEQUE ' + String(Math.floor(Math.random() * 900) + 100)}</td>
            <td>${txn.description}</td>
            <td>${txn.credit > 0 ? formatCurrency(txn.credit) : ''}</td>
            <td>${txn.debit > 0 ? formatCurrency(txn.debit) : ''}</td>
            <td>${formatCurrency(txn.balance)}</td>
          </tr>
        `
      )
      .join('');

    // Add TOTAL row for last page or if page ends
    const totalRow = `
      <tr style="background: #d3d3d3; font-weight: 700;">
        <td colspan="3" style="text-align: left;">TOTAL</td>
        <td style="text-align: right;">${isLastPage ? formatCurrency(totalCredit) : formatCurrency(pageCredit)}</td>
        <td style="text-align: right;">${isLastPage ? formatCurrency(totalDebit) : formatCurrency(pageDebit)}</td>
        <td style="text-align: right;">${formatCurrency(pageTxns[pageTxns.length - 1].balance)}</td>
      </tr>
    `;

    return `
    <div class="page">
      <div class="header-band"></div>
      <div class="page-number">Page ${pageNum} of ${totalPages}</div>
      
      <div class="header">
        <div class="logo-section">
          ${buildIciciLogo()}
        </div>
        ${isFirstPage ? `
        <div class="branch-info">
          <strong>Your Base Branch: ${details.bankBranch || 'ICICI BANK LTD., PEOPLES CAMPUS,'}</strong><br />
          PEOPLES MALL, BHANPUR, BHOPAL - 462037, 462037
        </div>
        ` : ''}
      </div>
      
      ${isFirstPage ? `
      <div style="padding: 0 12px;">
        <div class="barcode">
          <svg width="280" height="40" xmlns="http://www.w3.org/2000/svg">
            <rect x="0" y="0" width="2" height="40" fill="#000"/>
            <rect x="4" y="0" width="1" height="40" fill="#000"/>
            <rect x="7" y="0" width="3" height="40" fill="#000"/>
            <rect x="12" y="0" width="1" height="40" fill="#000"/>
            <rect x="15" y="0" width="2" height="40" fill="#000"/>
            <rect x="19" y="0" width="3" height="40" fill="#000"/>
            <rect x="24" y="0" width="1" height="40" fill="#000"/>
            <rect x="27" y="0" width="2" height="40" fill="#000"/>
            <rect x="31" y="0" width="1" height="40" fill="#000"/>
            <rect x="34" y="0" width="3" height="40" fill="#000"/>
            <rect x="39" y="0" width="2" height="40" fill="#000"/>
            <rect x="43" y="0" width="1" height="40" fill="#000"/>
            <rect x="46" y="0" width="2" height="40" fill="#000"/>
            <rect x="50" y="0" width="3" height="40" fill="#000"/>
            <rect x="55" y="0" width="1" height="40" fill="#000"/>
            <rect x="58" y="0" width="2" height="40" fill="#000"/>
            <rect x="62" y="0" width="1" height="40" fill="#000"/>
            <rect x="65" y="0" width="3" height="40" fill="#000"/>
            <rect x="70" y="0" width="2" height="40" fill="#000"/>
            <rect x="74" y="0" width="1" height="40" fill="#000"/>
          </svg>
        </div>
        
        <div class="customer-section">
          <div class="customer-name">${details.name.toUpperCase()}</div>
          <div class="customer-address">
            ${details.address?.toUpperCase() || 'NO 17,PURA MAN BHAWAN INTKHEDI,BHOPAL,'},<br />
            ${details.city?.toUpperCase() || 'BHOPAL'}<br />
            MADHYAPRADESH - INDIA - 462038
          </div>
        </div>
        
        <div class="contact-box">
          <div> Visit www.icicibank.com</div>
          <div> Dial your Bank B479</div>
        </div>
      </div>
      
      <div class="kyc-warning">
        Did you know? It's mandatory to be KYC compliant as per RBI guidelines. If you have not submitted your KYC documents, please visit the nearest branch or contact your Relationship Manager to complete KYC details for your Account.
      </div>
      
      <div class="summary-title">Summary of Accounts held under Cust ID: ${statement.id} as on ${formatDate(endDate)}</div>
      
      <div style="padding: 0 12px; font-size: 8pt; font-weight: 700; margin-bottom: 2px;">ACCOUNT DETAILS - INR</div>
      
      <div class="account-summary">
        <div class="summary-row" style="background: #d3d3d3;">
          <div class="summary-cell" style="font-weight: 700;">ACCOUNT TYPE</div>
          <div class="summary-cell numeric" style="font-weight: 700;">A/C BALANCE()</div>
          <div class="summary-cell numeric" style="font-weight: 700;">FIXED DEPOSITS (LINKED) BAL.()</div>
          <div class="summary-cell numeric" style="font-weight: 700;">TOTAL BALANCE( !!)</div>
          <div class="summary-cell" style="font-weight: 700;">NOMINATION</div>
        </div>
        <div class="summary-row">
          <div class="summary-cell">Savings A/c ${maskAccountNumber(details.accountNumber)}</div>
          <div class="summary-cell numeric">${formatCurrency(closingBalance)}</div>
          <div class="summary-cell numeric">0.00</div>
          <div class="summary-cell numeric">${formatCurrency(closingBalance)}</div>
          <div class="summary-cell">Registered</div>
        </div>
        <div class="summary-row" style="font-weight: 700;">
          <div class="summary-cell">TOTAL</div>
          <div class="summary-cell numeric">${formatCurrency(closingBalance)}</div>
          <div class="summary-cell numeric">0.00</div>
          <div class="summary-cell numeric">${formatCurrency(closingBalance)}</div>
          <div class="summary-cell"></div>
        </div>
      </div>
      
      <div class="statement-title">Statement of Transactions in Savings Account Number: ${details.accountNumber} in INR for the period ${formatStatementDate(startDate)} - ${formatStatementDate(endDate)}</div>
      ` : `
      <div style="padding: 0 12px;">
        <div class="customer-name">${details.name.toUpperCase()}</div>
      </div>
      `}
      
      <table>
        <thead>
          <tr>
            <th>DATE</th>
            <th>MODE**</th>
            <th>PARTICULARS</th>
            <th>DEPOSITS</th>
            <th>WITHDRAWALS</th>
            <th>BALANCE</th>
          </tr>
        </thead>
        <tbody>
          ${htmlTransactions}
          ${totalRow}
        </tbody>
      </table>
      
      ${isLastPage ? `
      <div class="footer">
        Statement period: ${formatStatementDate(startDate)} to ${formatStatementDate(endDate)} | Closing balance: ${formatCurrency(closingBalance)}
      </div>
      ` : ''}
    </div>
    `;
  };

  // Generate pages
  const pages = [];
  for (let i = 0; i < totalPages; i++) {
    const start = i * TRANSACTIONS_PER_PAGE;
    const end = Math.min(start + TRANSACTIONS_PER_PAGE, transactions.length);
    const pageTxns = transactions.slice(start, end);
    const isFirstPage = i === 0;
    const isLastPage = i === totalPages - 1;
    
    pages.push(buildPage(i + 1, pageTxns, isFirstPage, isLastPage));
  }

  return `<!DOCTYPE html>
  <html>
    <head>
      <meta charset="utf-8" />
      <title>ICICI Bank Statement</title>
      <style>
        @page { margin: 0.5cm; size: A4; }
        * { box-sizing: border-box; margin: 0; padding: 0; }
        body { 
          font-family: Arial, sans-serif; 
          font-size: 8pt; 
          line-height: 1.2;
          color: #000;
          background: #fff;
          padding: 0;
        }
        
        .header-band { 
          height: 24px; 
          background: linear-gradient(90deg, #d2691e, #ff6347);
          margin: 0;
        }
        
        .page-number {
          text-align: right;
          font-size: 8pt;
          padding: 8px 12px 0 0;
          font-weight: 600;
        }
        
        .header { 
          display: flex; 
          justify-content: space-between; 
          align-items: flex-start; 
          padding: 12px 12px 8px 12px;
        }
        
        .logo-section svg {
          width: 180px;
          height: auto;
        }
        
        .branch-info { 
          text-align: right; 
          font-size: 7pt; 
          line-height: 1.4;
        }
        
        .customer-section {
          padding: 0 12px;
          margin-bottom: 8px;
        }
        
        .customer-name {
          font-weight: 700;
          font-size: 8pt;
          margin-bottom: 2px;
        }
        
        .customer-address {
          font-size: 7pt;
          line-height: 1.3;
          margin-bottom: 4px;
        }
        
        .barcode {
          margin: 8px 0;
        }
        
        .contact-box {
          background: #d3d3d3;
          padding: 6px 10px;
          font-size: 7pt;
          margin: 8px 0;
          display: flex;
          justify-content: flex-end;
          gap: 20px;
        }
        
        .kyc-warning { 
          background: #d3d3d3; 
          padding: 6px 10px; 
          font-size: 7pt; 
          margin: 8px 12px;
          line-height: 1.4;
        }
        
        .summary-title {
          font-size: 8pt;
          font-weight: 700;
          padding: 8px 12px 4px 12px;
        }
        
        .account-summary {
          margin: 0 12px 8px 12px;
          border: 1px solid #000;
        }
        
        .summary-header { 
          background: #d3d3d3; 
          padding: 4px 8px; 
          font-size: 7pt; 
          font-weight: 700;
          text-transform: uppercase;
          border-bottom: 1px solid #000;
        }
        
        .summary-row {
          display: grid;
          grid-template-columns: 2fr 1.5fr 1.5fr 1.5fr 1fr;
          border-bottom: 1px solid #000;
        }
        
        .summary-row:last-child {
          border-bottom: none;
        }
        
        .summary-cell {
          padding: 4px 8px;
          font-size: 7pt;
          border-right: 1px solid #000;
          text-align: left;
        }
        
        .summary-cell:last-child {
          border-right: none;
        }
        
        .summary-cell.numeric {
          text-align: right;
        }
        
        .statement-title {
          font-size: 8pt;
          font-weight: 700;
          padding: 8px 12px 4px 12px;
        }
        
        table { 
          width: calc(100% - 24px);
          margin: 0 12px;
          border-collapse: collapse; 
          font-size: 6.5pt;
        }
        
        thead th { 
          background: #d3d3d3; 
          color: #000;
          border: 1px solid #000;
          padding: 3px 4px;
          font-weight: 700;
          text-align: left;
          font-size: 6.5pt;
          line-height: 1.2;
        }
        
        tbody td {
          border: 1px solid #000;
          padding: 2px 4px;
          font-size: 6pt;
          line-height: 1.3;
          vertical-align: top;
        }
        
        tbody td:nth-child(1) {
          text-align: center;
          width: 8%;
        }
        
        tbody td:nth-child(2) {
          text-align: left;
          width: 12%;
        }
        
        tbody td:nth-child(3) {
          text-align: left;
          width: 40%;
        }
        
        tbody td:nth-child(4) {
          text-align: right;
          width: 13%;
        }
        
        tbody td:nth-child(5) {
          text-align: right;
          width: 13%;
        }
        
        tbody td:nth-child(6) {
          text-align: right;
          width: 14%;
        }
        
        .footer { 
          margin-top: 12px;
          padding: 0 12px;
          font-size: 7pt; 
          color: #555;
        }
        
        .page {
          page-break-after: always;
          min-height: 100vh;
        }
        
        .page:last-child {
          page-break-after: auto;
        }
      </style>
    </head>
    <body>
      ${pages.join('')}
    </body>
  </html>`;
};
